interface FloatingIconProps {
    icon: typeof techIcons[0];
    mouseX: number;
    mouseY: number;
}

function FloatingIcon({ icon, mouseX, mouseY }: FloatingIconProps) {
    const x = useSpring(0, { stiffness: 50, damping: 20 });
    const y = useSpring(0, { stiffness: 50, damping: 20 });
    const rotate = useSpring(0, { stiffness: 30, damping: 15 });

    useEffect(() => {
        // Calculate offset based on mouse position and depth
        const offsetX = (mouseX - 50) * icon.depth * 0.5;
        const offsetY = (mouseY - 50) * icon.depth * 0.5;
        const rotation = (mouseX - 50) * icon.depth * 0.1;

        x.set(offsetX);
        y.set(offsetY);
        rotate.set(rotation);
    }, [mouseX, mouseY, icon.depth, x, y, rotate]);

    return (
        <motion.div
            className="absolute pointer-events-none"
            style={{
                left: `${icon.position.x}%`,
                top: `${icon.position.y}%`,
                x,
                y,
                rotate,
                width: icon.size,
                height: icon.size,
            }}
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 0.6, scale: 1 }}
            transition={{ delay: Math.random() * 0.5, duration: 0.8, ease: 'easeOut' }}
        >
            {/* Glow effect */}
            <div
                className="absolute inset-0 rounded-2xl blur-xl opacity-50"
                style={{ backgroundColor: icon.color }}
            />

            {/* Icon container */}
            <motion.div
                className="relative w-full h-full rounded-2xl bg-white/5 backdrop-blur-sm border border-white/10 flex items-center justify-center"
                animate={{
                    y: [0, -10, 0],
                }}
                transition={{
                    duration: 3 + Math.random() * 2,
                    repeat: Infinity,
                    ease: 'easeInOut',
                }}
            >
                <div
                    className="w-3/4 h-3/4"
                    style={{ color: icon.color }}
                    dangerouslySetInnerHTML={{ __html: icon.svg }}
                />
            </motion.div>
        </motion.div>
    );
}

export function HeroScene() {
    const containerRef = useRef<HTMLDivElement>(null);
    const [mousePosition, setMousePosition] = useState({ x: 50, y: 50 });

    useEffect(() => {
        const handleMouseMove = (e: MouseEvent) => {
            if (!containerRef.current) return;

            const rect = containerRef.current.getBoundingClientRect();
            const x = ((e.clientX - rect.left) / rect.width) * 100;
            const y = ((e.clientY - rect.top) / rect.height) * 100;

            setMousePosition({ x, y });
        };

        window.addEventListener('mousemove', handleMouseMove);
        return () => window.removeEventListener('mousemove', handleMouseMove);
    }, []);

    return (
        <div
            ref={containerRef}
            className="absolute inset-0 overflow-hidden pointer-events-none"
            aria-hidden="true"
        >
            {/* Floating icons */}
            {techIcons.map((icon) => (
                <FloatingIcon
                    key={icon.name}
                    icon={icon}
                    mouseX={mousePosition.x}
                    mouseY={mousePosition.y}
                />
            ))}

            {/* Central glow */}
            <motion.div
                className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[600px] h-[600px] rounded-full"
                style={{
                    background: 'radial-gradient(circle, rgba(139, 92, 246, 0.15) 0%, rgba(6, 182, 212, 0.1) 30%, transparent 70%)',
                }}
                animate={{
                    scale: [1, 1.1, 1],
                    opacity: [0.5, 0.7, 0.5],
                }}
                transition={{
                    duration: 8,
                    repeat: Infinity,
                    ease: 'easeInOut',
                }}
            />
        </div>
    );
}
